import React, { Component } from 'react';
import Header from './Header';
import ShoppingList from './ShoppingList';
import InputItem from './InputItem';

class ShoppingApp extends Component{
    constructor (props) {
      super(props);
      this.addItem = this.addItem.bind(this);
      this.removeItem = this.removeItem.bind(this);
      this.markDone = this.markDone.bind(this);
      this.state = {shoppinglist: []};
    }
    addItem(item) {
      shoppinglist.unshift({
        id: shoppinglist.length+1, 
        name: item.newItemValue, 
        done: false
      });
      this.setState({shoppinglist: shoppinglist});
    }
    removeItem (id) {
      shoppinglist.splice(id, 1);
      this.setState({shoppinglist: shoppinglist});
    }
    markDone(id) {
      var item = shoppinglist[id];
      console.log(id);
      shoppinglist.splice(id, 1);
      item.done = !item.done;
      console.log(item);
      item.done ? shoppinglist.push(item) : shoppinglist.unshift(item);
      this.setState({shoppinglist: shoppinglist});  
    }
    render() {
      return (
        <div id="main">
          <Header />
          <ShoppingList items={this.props.initItems} removeItem={this.removeItem} markDone={this.markDone}/>
          <InputItem addItem={this.addItem} />
        </div>
      );
    }
}

export default ShoppingApp;
