import React, { Component } from 'react';
import ShoppingList from './ShoppingList';
import './App.css';

class App extends Component {
  constructor(){
    super();
    this.state = {
      items: [],
      currentItem: {
        id:'',
        name: '',
        supermarket:''
      }
    }
  }
  handleSubmit (e){
    let item = e.target.value;
    this.setState({
      currentItem : item
    })
  }
  render() {
    return (
      <div className="App">
        <ShoppingList/>
      </div>
    );
  }
}

export default App;
